const easyTab=function(t,e){let a=(t=document.getElementById(t)).querySelectorAll(".easytab-tab"),s=a.length,c=t.querySelectorAll(".easytab-content");null==e?(a[0].classList.add("active"),c[0].classList.add("active")):(a[e-1].classList.add("active"),c[e-1].classList.add("active"));for(let t=0;t<s;t++){let e=a[t],l=c[t];e.addEventListener("click",(function(){for(let t=0;t<s;t++)a[t].classList.remove("active"),c[t].classList.remove("active");e.classList.add("active"),l.classList.add("active")}))}};