/* https://github.com/babakfp/easy-tab
*/
const easytab=e=>{let t=document.getElementById(e),c=t.querySelector(".tab-box"),l=c.childElementCount,r=c.querySelectorAll(".tab"),s=t.querySelector(".content-box"),a=s.childElementCount,n=s.querySelectorAll(".content");for(let e=0;e<l;e++){let t=r[e],c=n[e];t.id=t.className+e,c.id=c.className+e,r[0].classList.add("current"),n[0].classList.add("current"),t.addEventListener("click",()=>{for(let e=0;e<a;e++)r[e].classList.remove("current"),n[e].classList.remove("current");t.classList.add("current"),c.classList.add("current")})}};
