const easytab=e=>{let t=document.querySelector(e),c=t.querySelector(".tab-box"),r=c.childElementCount,l=c.querySelectorAll(".tab"),s=t.querySelector(".content-box"),a=s.childElementCount,n=s.querySelectorAll(".content");for(let e=0;e<r;e++){let t=l[e],c=n[e];t.id=t.className+e,c.id=c.className+e,l[0].classList.add("current"),n[0].classList.add("current"),t.addEventListener("click",()=>{for(let e=0;e<a;e++)l[e].classList.remove("current"),n[e].classList.remove("current");t.classList.add("current"),c.classList.add("current")})}};