const easytab=(t,e)=>{let a=document.getElementById(t),s=a.querySelector(".easytab-tab-list").querySelectorAll(".easytab-tab"),l=s.length,c=a.querySelector(".easytab-content-list").querySelectorAll(".easytab-content");for(let t=0;t<l;t++){let a=s[t],i=c[t];0==e||null==e?(s[0].classList.add("active"),c[0].classList.add("active")):(s[e-1].classList.add("active"),c[e-1].classList.add("active")),a.addEventListener("click",()=>{for(let t=0;t<l;t++)s[t].classList.remove("active"),c[t].classList.remove("active");a.classList.add("active"),i.classList.add("active")})}};
